package Project;

public class Demoobject {
	
	private String name="shivaranjan";
	private String email;
	private String pnum;
	
	
	public Demoobject() {
		super();
		// TODO Auto-generated constructor stub
	}


	public Demoobject(String name, String email, String pnum) {
		super();
		this.name = name;
		this.email = email;
		this.pnum = pnum;
	}


	public String getName() {
		return name;
	}


	public void setName(String name) {
		this.name = name;
	}


	public String getEmail() {
		return email;
	}


	public void setEmail(String email) {
		this.email = email;
	}


	public String getPnum() {
		return pnum;
	}


	public void setPnum(String pnum) {
		this.pnum = pnum;
	}
	
	
	

}
